import jsTPS_Transaction from "../common/jsTPS.js"
/**
 * MoveSong_Transaction
 * 
 * This class represents a transaction that works with drag
 * and drop. It will be managed by the transaction stack.
 * 
 * @author McKilla Gorilla
 * @author safwankader
 */
export default class AddSong_Transaction extends jsTPS_Transaction {
    constructor(initStore, oldSong, newTitle, newArtist, newLink) {
        super();
        this.store = initStore;
        this.oldSong = JSON.parse(JSON.stringify(oldSong));
        this.title = newTitle;
        this.artist = newArtist;
        this.link = newLink;

    }

    doTransaction() {
        this.store.addSong();
    }
    
    undoTransaction() {
        let newSongs = this.store.currentList.songs;
        let deleteSong = newSongs.pop();
        this.store.deleteSongId = deleteSong._id;
        this.store.deletSongName = deleteSong.name;
        this.store.deleteSong();

    }
}
