import jsTPS_Transaction from "../common/jsTPS.js"
/**
 * MoveSong_Transaction
 * 
 * This class represents a transaction that works with drag
 * and drop. It will be managed by the transaction stack.
 * 
 * @author McKilla Gorilla
 * @author safwankader
 */
export default class AddSong_Transaction extends jsTPS_Transaction {
    constructor(initStore,deleteIndex) {
        super();
        this.store = initStore;
        this.index = deleteIndex;
        this.oldSong = null;

    }

    doTransaction() {
        this.oldSong = this.store.currentList.songs[this.deleteIndex];
        this.store.deleteMarkedSong();
    }
    
    undoTransaction() {
        let playlist = this.store.currentList;
        playlist.songs.splice(this.deleteIndex,0,this.oldSong);
        this.store.updateCurrentList(playlist.name,playlist.songs);
        

    }
}
